insert  into `sle_info`(`id`,`name`,`description`,`content`,`product`,`type`,`avatar`,`status`,`delete`,`created`,`updated`) values (1,'Mâm xe','','Với thiết kế mới, mâm đúc 15 inch mang đến diện mạo cá tính mạnh mẽ đầy thu hút.',40,91,'2021/12/ccf8eb8a5bcae721afb472db6e5e2fff.png',1,0,'2021-11-19 16:35:49','2021-12-07 13:57:06'),(2,'Gương xe','','Gương chiếu hậu được cải tiến với chức năng điều chỉnh điện ,chức năng gập điện và tích hợp báo rẽ tạo nên sự tiện nghi, dễ dàng hơn hết cho lái xe.',40,91,'2021/12/125c427be0b9cf78d19ae3ae04a7b0c5.jpg',1,0,'2021-12-07 13:58:15',NULL),(3,'Đuôi xe','','Đuôi xe là sự kết hợp tương phản giữa cụm đèn sau, đèn sương mù hẹp, trải dài qua hai bên cùng cản sau lớn đem đến cảm giác thể thao và mạnh mẽ nhưng cũng không kém phần tinh tế, sang trọng. Ngoài ra, xe còn tích hợp thêm camera lùi giúp lùi xe thuận tiện và an toàn.',40,91,'2021/12/fe8292c32ed759c36ff2bdb64c8e3d6b.jpg',1,0,'2021-12-07 13:59:05',NULL),(4,'Đầu xe','','Đường nét thiết kế sắc sảo của cụm đèn trước, hệ thống lưới tản nhiệt với thiết kế bậc thang trải dài liền mạch kết hợp cùng phần đèn sương mù hai bên tăng thêm vẻ bề thế từ góc nhìn chính diện.',40,91,'2021/12/ff6090c354c859146563e7a57f66a21a.png',1,0,'2021-12-07 13:59:29',NULL),(5,'Khoang hành lý','','Khoang hành lý được thiết kế tối đa hóa diện tích, đáp ứng mọi nhu cầu sử dụng của khách hàng.',40,92,'2021/12/48241fb31682d0284227421960dcf242.png',1,0,'2021-12-08 09:14:24',NULL),(6,'Hàng ghế sau','','Hàng ghế sau được thiết kế tạo cho hành khách tư thế ngồi thoải mái hơn, đồng thời giảm áp lực tối đa lên toàn bộ cơ thể. Ghế sau có thể gập 60:40 dễ dàng giúp việc chuyên chở các hành lý cồng kềnh dễ dàng hơn bao giờ hết.',40,92,'2021/12/b3c4ff997b9775d40ea7ab6405df0796.jpg',1,0,'2021-12-08 09:14:55',NULL),(7,'Truyền cảm hứng từ tiện nghi và thoải mái','','Trải nghiệm không gian nội thất tinh tế, sang trọng với ngôn ngữ thiết kế hiện đại. Bảng điều khiển trung tâm với điểm nhấn là những đường mạ bạc liền mạch theo dạng dòng thác chảy từ trên xuống',40,92,'2021/12/c28b7940fc1bea8f9be2df4f5f61be95.png',1,0,'2021-12-08 09:15:37',NULL),(8,'Kiểm soát hành trình','','Tính năng kiểm soát hành trình giúp thiết lập và duy trì tốc độ mong muốn của người lái mà không cần nhấn ga, hỗ trợ người lái thoải mái, chủ động hơn trong việc vận hành xe trên những cung đường dài. Tính năng cũng rất an toàn khi có tình huống bất ngờ xảy ra, người lái xe chỉ cần chạm nhẹ chân phanh, tính năng sẽ tự động ngắt và người lái có thể chủ động điều khiển tốc độ của xe thông qua chân ga như bình thường.',40,92,'2021/12/0447853609633f93614d9d71abd44519.png',1,0,'2021-12-08 09:16:49',NULL);

